const RecipeList = ({ recipes, onEdit, onDelete }) => {
  if (!recipes) {
    recipes = []; // Initialize with an empty array if recipes prop is undefined
  }

  const renderRecipeTable = () => {
    return (
      <table className="table mt-5">
        {/* The rest of your table JSX */}
      </table>
    );
  };

  const renderNoRecipesMessage = () => {
    return <p>No recipes found.</p>;
  };

  return (
    <div>
      {recipes.length > 0 ? renderRecipeTable() : renderNoRecipesMessage()}
    </div>
  );
};

export default RecipeList;
