import React, { useState } from 'react';
import RecipeForm from './RecipeForm';

const RecipeList = ({ user, recipes, setRecipes }) => {
  const [editingRecipeId, setEditingRecipeId] = useState(null);
  const [editedRecipe, setEditedRecipe] = useState({
    title: '',
    description: '',
    ingredients: '',
  });

  const handleDeleteRecipe = (recipeId) => {
    const updatedRecipes = recipes.filter((recipe) => recipe.id !== recipeId);
    setRecipes(updatedRecipes);
  };

  const handleEditRecipe = (recipeId) => {
    const recipeToEdit = recipes.find((recipe) => recipe.id === recipeId);
    if (recipeToEdit) {
      setEditingRecipeId(recipeId);
      setEditedRecipe({
        title: recipeToEdit.title,
        description: recipeToEdit.description,
        ingredients: recipeToEdit.ingredients,
      });
    }
  };

  const handleUpdateRecipe = (e) => {
    e.preventDefault();
    // Perform validation or any additional logic here
    const updatedRecipes = recipes.map((recipe) => {
      if (recipe.id === editingRecipeId) {
        return {
          ...recipe,
          title: editedRecipe.title,
          description: editedRecipe.description,
          ingredients: editedRecipe.ingredients,
        };
      }
      return recipe;
    });
    setRecipes(updatedRecipes);
    setEditingRecipeId(null);
    setEditedRecipe({
      title: '',
      description: '',
      ingredients: '',
    });
  };

  const handleCancelEdit = () => {
    setEditingRecipeId(null);
    setEditedRecipe({
      title: '',
      description: '',
      ingredients: '',
    });
  };

  const handleAddRecipe = (newRecipe) => {
    const updatedRecipes = [...recipes, newRecipe];
    setRecipes(updatedRecipes);
  };

  if (recipes.length === 0) {
    return (
      <div>
        <p>No recipes added yet.</p>
        <RecipeForm onSubmit={handleAddRecipe} setRecipes={setRecipes} />
      </div>
    );
  }

  return (
    <div>
      {recipes.map((recipe) => (
        <div key={recipe.id}>
          {editingRecipeId === recipe.id ? (
            <form onSubmit={handleUpdateRecipe}>
              {/* Form fields */}
            </form>
          ) : (
            <>
              <h3>{recipe.title}</h3>
              <p>{recipe.description}</p>
              <p>{recipe.ingredients}</p>
            </>
          )}
          <button onClick={() => handleDeleteRecipe(recipe.id)}>Delete</button>
          <button onClick={() => handleEditRecipe(recipe.id)}>Edit</button>
        </div>
      ))}
      <RecipeForm onSubmit={handleAddRecipe} setRecipes={setRecipes} />
    </div>
  );
};

export default RecipeList;
