import './App.css';
import React, { useState, useEffect } from 'react';
import { collection, addDoc, deleteDoc, updateDoc, doc, onSnapshot } from 'firebase/firestore';
import 'bootstrap/dist/css/bootstrap.min.css';
import db from './firebase';

const RecipeForm = ({ onSubmit }) => {
  const [title, setTitle] = useState('');
  const [description, setDescription] = useState('');
  const [ingredients, setIngredients] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    const recipe = { title, description, ingredients };
    try {
      const docRef = await addDoc(collection(db, 'recipes'), recipe);
      recipe.id = docRef.id;
      onSubmit(recipe);
      setTitle('');
      setDescription('');
      setIngredients('');
    } catch (error) {
      console.error('Error adding recipe:', error);
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      {/* Rest of the form code */}
    </form>
  );
};

const RecipeItem = ({ recipe, onEdit, onDelete }) => {
  const handleEdit = () => {
    onEdit(recipe);
  };

  const handleDelete = async () => {
    try {
      await deleteDoc(doc(db, 'recipes', recipe.id));
      onDelete(recipe);
    } catch (error) {
      console.error('Error deleting recipe:', error);
    }
  };

  return (
    <tr>
      {/* Recipe item content */}
      <td>
        <button className="btn btn-warning btn-sm me-2" onClick={handleEdit}>
          Edit
        </button>
        <button className="btn btn-danger btn-sm" onClick={handleDelete}>
          Delete
        </button>
      </td>
    </tr>
  );
};

const RecipeList = ({ recipes, onEdit, onDelete }) => {
  // Recipe list code remains the same
};

const App = () => {
  const [recipes, setRecipes] = useState([]);

  useEffect(() => {
    const unsubscribe = onSnapshot(collection(db, 'recipes'), (snapshot) => {
      const recipesData = snapshot.docs.map((doc) => ({
        id: doc.id,
        ...doc.data(),
      }));
      setRecipes(recipesData);
    });

    return () => unsubscribe();
  }, []);

  const addRecipe = (recipe) => {
    setRecipes([...recipes, recipe]);
  };

  const deleteRecipe = (recipe) => {
    setRecipes(recipes.filter((r) => r.id !== recipe.id));
  };

  const editRecipe = (recipe) => {
    setEditingRecipe(recipe);
  };

  const updateRecipe = async (recipe) => {
    try {
      await updateDoc(doc(db, 'recipes', recipe.id), recipe);
      setRecipes([...recipes.filter((r) => r.id !== recipe.id), recipe]);
      setEditingRecipe(null);
    } catch (error) {
      console.error('Error updating recipe:', error);
    }
  };

  const [editingRecipe, setEditingRecipe] = useState(null);

  return (
    <div className="container my-5">
      {/* Rest of the code remains the same */}
    </div>
  );
};

export default App;
