import './App.css';
import React, { useState, useEffect } from 'react';
import firebase from 'firebase/app';
import 'firebase/firestore';
import 'bootstrap/dist/css/bootstrap.min.css';
import { initializeApp } from "firebase/app";



const firebaseConfig = {
  apiKey: "AIzaSyDSgl8BqRe9HPcfcmCcterVOuK4ckY1Ihc",
  authDomain: "task-list-7bb3b.firebaseapp.com",
  projectId: "task-list-7bb3b",
  storageBucket: "task-list-7bb3b.appspot.com",
  messagingSenderId: "439576102041",
  appId: "1:439576102041:web:596a1f4b8bc1cdc21ca408"
};

firebase.initializeApp(firebaseConfig);
const db = firebase.firestore();

const RecipeForm = ({ onSubmit }) => {
  const [title, setTitle] = useState('');
  const [description, setDescription] = useState('');
  const [ingredients, setIngredients] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();
    onSubmit({ title, description, ingredients });
    setTitle('');
    setDescription('');
    setIngredients('');
  };

  return (
    <form onSubmit={handleSubmit}>
      {/* Form inputs */}
    </form>
  );
};

const RecipeItem = ({ recipe, onEdit, onDelete }) => {
  return (
    <tr>
      {/* Recipe item */}
    </tr>
  );
};

const RecipeList = ({ recipes, onEdit, onDelete }) => {
  return (
    <table className="table mt-5">
      <thead>
        {/* Table headers */}
      </thead>
      <tbody>
        {/* Recipe items */}
      </tbody>
    </table>
  );
};

const App = () => {
  const [recipes, setRecipes] = useState([]);
  const [editingRecipe, setEditingRecipe] = useState(null);

  useEffect(() => {
    // Fetch recipes from Firebase
    const fetchRecipes = async () => {
      const snapshot = await db.collection('recipes').get();
      const data = snapshot.docs.map((doc) => ({ id: doc.id, ...doc.data() }));
      setRecipes(data);
    };

    fetchRecipes();
  }, []);

  const addRecipe = async (recipe) => {
    const docRef = await db.collection('recipes').add(recipe);
    setRecipes([...recipes, { id: docRef.id, ...recipe }]);
  };

  const deleteRecipe = async (recipe) => {
    await db.collection('recipes').doc(recipe.id).delete();
    setRecipes(recipes.filter((r) => r.id !== recipe.id));
  };

  const editRecipe = (recipe) => {
    deleteRecipe(recipe);
    setEditingRecipe(recipe);
  };

  const updateRecipe = async (recipe) => {
    await db.collection('recipes').doc(recipe.id).set(recipe);
    setRecipes([...recipes, recipe]);
    setEditingRecipe(null);
  };

  return (
    <div className="container my-5">
      <div className="card p-4">
        <h1>Recipe Book</h1>
        <RecipeForm onSubmit={addRecipe} />
        <RecipeList
          recipes={recipes}
          onEdit={editRecipe}
          onDelete={deleteRecipe}
        />
        {editingRecipe && (
          <div className="mt-4">
            <h2>Edit Recipe</h2>
            <RecipeForm onSubmit={updateRecipe} />
          </div>
        )}
      </div>
    </div>
  );
};

export default App;
