import React, { useState } from 'react';

const RecipeList = ({ recipes, onEdit, onDelete }) => {
  const [newRecipe, setNewRecipe] = useState({ title: '', description: '', ingredients: '' });

  const handleInputChange = (event) => {
    const { name, value } = event.target;
    setNewRecipe((prevRecipe) => ({ ...prevRecipe, [name]: value }));
  };

  const handleAddRecipe = () => {
    // Add logic to handle adding a new recipe, such as calling an API or updating the state
    // Here, you can use the `newRecipe` state to access the newly added recipe details
    // For example:
    // addRecipe(newRecipe);

    // Reset the newRecipe state after adding the recipe
    setNewRecipe({ title: '', description: '', ingredients: '' });
  };

  return (
    <div>
      <table className="table mt-5">
        <thead>
          <tr>
            <th>Title</th>
            <th>Description</th>
            <th>Ingredients</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {recipes.length > 0 ? (
            recipes.map((recipe, index) => (
              <tr key={index}>
                <td>{recipe.title}</td>
                <td>{recipe.description}</td>
                <td>{recipe.ingredients}</td>
                <td>
                  <button className="btn btn-warning btn-sm me-2" onClick={() => onEdit(recipe.id)}>
                    Edit
                  </button>
                  <button className="btn btn-danger btn-sm" onClick={() => onDelete(recipe.id)}>
                    Delete
                  </button>
                </td>
              </tr>
            ))
          ) : (
            <tr>
              <td colSpan="4">No recipes found.</td>
            </tr>
          )}
        </tbody>
      </table>

      <h3>Add Recipe</h3>
      <form>
        <div className="mb-3">
          <label htmlFor="title" className="form-label">
            Title
          </label>
          <input
            type="text"
            className="form-control"
            id="title"
            name="title"
            value={newRecipe.title}
            onChange={handleInputChange}
          />
        </div>
        <div className="mb-3">
          <label htmlFor="description" className="form-label">
            Description
          </label>
          <textarea
            className="form-control"
            id="description"
            name="description"
            value={newRecipe.description}
            onChange={handleInputChange}
          ></textarea>
        </div>
        <div className="mb-3">
          <label htmlFor="ingredients" className="form-label">
            Ingredients
          </label>
          <input
            type="text"
            className="form-control"
            id="ingredients"
            name="ingredients"
            value={newRecipe.ingredients}
            onChange={handleInputChange}
          />
        </div>
        <button type="button" className="btn btn-primary" onClick={handleAddRecipe}>
          Add Recipe
        </button>
      </form>
    </div>
  );
};

export default RecipeList;
