import React, { useState } from 'react';

const RecipeList = ({ recipes, onEdit, onDelete }) => {
  const [newRecipe, setNewRecipe] = useState({ title: '', description: '', ingredients: '' });

  const handleInputChange = (event) => {
    const { name, value } = event.target;
    setNewRecipe((prevRecipe) => ({ ...prevRecipe, [name]: value }));
  };

  const handleAddRecipe = () => {
    // Validate if any of the fields are empty
    if (!newRecipe.title || !newRecipe.description || !newRecipe.ingredients) {
      alert('Please fill in all fields');
      return;
    }

    // Create a new recipe object
    const recipe = { ...newRecipe, id: generateUniqueId() };

    // Update the recipes state by adding the new recipe
    setRecipes((prevRecipes) => [...prevRecipes, recipe]);

    // Reset the newRecipe state after adding the recipe
    setNewRecipe({ title: '', description: '', ingredients: '' });
  };

  const handleDeleteRecipe = (recipeId) => {
    // Filter out the recipe with the given id
    const updatedRecipes = recipes.filter((recipe) => recipe.id !== recipeId);

    // Update the recipes state with the updated array
    setRecipes(updatedRecipes);
  };

  return (
    <div>
      {recipes && recipes.length > 0 ? (
        <table className="table mt-5">
          <thead>
            <tr>
              <th>Title</th>
              <th>Description</th>
              <th>Ingredients</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {recipes.map((recipe, index) => (
              <tr key={index}>
                <td>{recipe.title}</td>
                <td>{recipe.description}</td>
                <td>{recipe.ingredients}</td>
                <td>
                  <button className="btn btn-warning btn-sm me-2" onClick={() => onEdit(recipe.id)}>
                    Edit
                  </button>
                  <button className="btn btn-danger btn-sm" onClick={() => handleDeleteRecipe(recipe.id)}>
                    Delete
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      ) : (
        <p>No recipes found. Add a recipe.</p>
      )}

      <h3>Add Recipe</h3>
      <form>
        {/* Form fields */}
        <div className="mb-3">
          <label className="form-label">Title</label>
          <input
            type="text"
            className="form-control"
            name="title"
            value={newRecipe.title}
            onChange={handleInputChange}
          />
        </div>
        <div className="mb-3">
          <label className="form-label">Description</label>
          <textarea
            className="form-control"
            name="description"
            value={newRecipe.description}
            onChange={handleInputChange}
          ></textarea>
        </div>
        <div className="mb-3">
          <label className="form-label">Ingredients</label>
          <textarea
            className="form-control"
            name="ingredients"
            value={newRecipe.ingredients}
            onChange={handleInputChange}
          ></textarea>
        </div>
        <button type="button" className="btn btn-primary" onClick={handleAddRecipe}>
          Add Recipe
        </button>
      </form>
    </div>
  );
};

export default RecipeList;
