import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Route, Redirect } from 'react-router-dom';
import { initializeApp } from 'firebase/app';
import {
  getAuth,
  createUserWithEmailAndPassword,
  signInWithEmailAndPassword,
  signOut,
  onAuthStateChanged,
} from 'firebase/auth';
import 'bootstrap/dist/css/bootstrap.min.css';
import 'bootstrap-icons/font/bootstrap-icons.css';
import RecipeForm from './components/RecipeForm';
import RecipeList from './components/RecipeList';
import Register from './components/Register';
import Login from './components/Login';
import { dbConfig } from './firebase';

const app = initializeApp(dbConfig);
const auth = getAuth(app);

function App() {
  const [user, setUser] = useState(null);

  useEffect(() => {
    const unsubscribe = onAuthStateChanged(auth, (user) => {
      if (user) {
        setUser(user);
      } else {
        setUser(null);
      }
    });

    return () => unsubscribe();
  }, []);

  const handleRegister = async (email, password) => {
    try {
      await createUserWithEmailAndPassword(auth, email, password);
    } catch (error) {
      console.error('Error registering:', error);
    }
  };

  const handleLogin = async (email, password) => {
    try {
      await signInWithEmailAndPassword(auth, email, password);
    } catch (error) {
      console.error('Error logging in:', error);
    }
  };

  const handleLogout = async () => {
    try {
      await signOut(auth);
    } catch (error) {
      console.error('Error logging out:', error);
    }
  };

  const PrivateRoute = ({ component: Component, ...rest }) => (
    <Route
      {...rest}
      render={(props) =>
        user ? (
          <Component {...props} user={user} />
        ) : (
          <Redirect to="/login" />
        )
      }
    />
  );

  return (
    <Router>
      <div className="container my-5">
        {user ? (
          <button className="btn btn-secondary mb-3" onClick={handleLogout}>
            Logout
          </button>
        ) : null}
        <Route
          path="/register"
          render={(props) => (
            <Register {...props} handleRegister={handleRegister} />
          )}
        />
        <Route
          path="/login"
          render={(props) => (
            <Login {...props} handleLogin={handleLogin} />
          )}
        />
        <PrivateRoute
          path="/"
          exact
          component={RecipeList}
        />
        <PrivateRoute
          path="/add"
          component={RecipeForm}
        />
      </div>
    </Router>
  );
}

export default App;
