import './App.css';
import { useState, useEffect } from 'react';
import firebase from 'firebase/app';
import 'firebase/firestore';


// Import the Firebase configuration object
import firebaseConfig from './firebaseConfig';

// Initialize Firebase
firebase.initializeApp(firebaseConfig);

const db = firebase.firestore();

function App() {
  const [recipes, setRecipes] = useState([]);
  const [recipeToEdit, setRecipeToEdit] = useState(null);

  useEffect(() => {
    const unsubscribe = db.collection('recipes').onSnapshot((snapshot) => {
      const recipesData = snapshot.docs.map((doc) => ({
        id: doc.id,
        ...doc.data()
      }));
      setRecipes(recipesData);
    });

    return () => unsubscribe();
  }, []);

  function onRecipeCreated(recipe) {
    setRecipeToEdit(null);
    db.collection('recipes').add(recipe);
  }

  function onRecipeDelete(recipe) {
    db.collection('recipes').doc(recipe.id).delete();
  }

  function onRecipeEdit(recipe) {
    setRecipeToEdit(recipe);
    db.collection('recipes').doc(recipe.id).delete();
  }

  return (
    <div className="text-center m-5">
      <div className="card p-4">
        <RecipeForm
          recipeToEdit={recipeToEdit}
          onRecipeCreated={onRecipeCreated}
        ></RecipeForm>
        <RecipeTable
          recipes={recipes}
          onRecipeEdit={onRecipeEdit}
          onRecipeDelete={onRecipeDelete}
        ></RecipeTable>
      </div>
    </div>
  );
}

export default App;
