import './App.css';
import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Route, Switch, Redirect } from 'react-router-dom';
import { collection, addDoc, deleteDoc, updateDoc, doc, onSnapshot } from 'firebase/firestore';
import { getAuth, createUserWithEmailAndPassword, signInWithEmailAndPassword, onAuthStateChanged } from 'firebase/auth';
import 'bootstrap/dist/css/bootstrap.min.css';
import 'bootstrap-icons/font/bootstrap-icons.css';
import RecipeForm from './components/RecipeForm';
import RecipeList from './components/RecipeList';
import RegisterPage from './components/RegisterPage';
import LoginPage from './components/LoginPage';
import { db, auth } from './firebase';

function App() {
  const [recipes, setRecipes] = useState([]);
  const [user, setUser] = useState(null);

  useEffect(() => {
    const unsubscribe = onSnapshot(collection(db, 'recipes'), (snapshot) => {
      const recipesData = snapshot.docs.map((doc) => ({
        id: doc.id,
        ...doc.data(),
      }));
      setRecipes(recipesData);
    });

    const authUnsubscribe = onAuthStateChanged(auth, (user) => {
      setUser(user);
    });

    return () => {
      unsubscribe();
      authUnsubscribe();
    };
  }, []);

  const addRecipe = async (recipe) => {
    try {
      const docRef = await addDoc(collection(db, 'recipes'), recipe);
      recipe.id = docRef.id;
      setRecipes([...recipes, recipe]);
    } catch (error) {
      console.error('Error adding recipe:', error);
    }
  };

  const deleteRecipe = async (recipeId) => {
    try {
      await deleteDoc(doc(db, 'recipes', recipeId));
      setRecipes(recipes.filter((recipe) => recipe.id !== recipeId));
    } catch (error) {
      console.error('Error deleting recipe:', error);
    }
  };

  const updateRecipe = async (recipe) => {
    try {
      await updateDoc(doc(db, 'recipes', recipe.id), recipe);
      setRecipes(
        recipes.map((r) => (r.id === recipe.id ? { ...r, ...recipe } : r))
      );
    } catch (error) {
      console.error('Error updating recipe:', error);
    }
  };

  const register = async (email, password) => {
    try {
      await createUserWithEmailAndPassword(auth, email, password);
    } catch (error) {
      console.error('Error registering:', error);
    }
  };

  const login = async (email, password) => {
    try {
      await signInWithEmailAndPassword(auth, email, password);
    } catch (error) {
      console.error('Error logging in:', error);
    }
  };

  return (
    <Router>
      <div className="container my-5">
        <div className="card p-4">
          <h1>Recipe Book</h1>

          <Switch>
            <Route exact path="/register">
              {user ? <Redirect to="/" /> : <RegisterPage onRegister={register} />}
            </Route>

            <Route exact path="/login">
              {user ? <Redirect to="/" /> : <LoginPage onLogin={login} />}
            </Route>

            <Route exact path="/">
              {user ? (
                <>
                  <RecipeForm onSubmit={addRecipe} />
                  <RecipeList
                    recipes={recipes}
                    onDelete={deleteRecipe}
                    onEdit={updateRecipe}
                  />
                </>
              ) : (
                <Redirect to="/login" />
              )}
            </Route>
          </Switch>
        </div>
      </div>
    </Router>
  );
}

export default App;
