import React, { useState } from 'react';
import { v4 as uuidv4 } from 'uuid';
import RecipeForm from './RecipeForm';

const RecipeList = ({ user }) => {
  const [recipes, setRecipes] = useState([]);

  const handleAddRecipe = (newRecipe) => {
    // Validate if any of the fields are empty
    if (!newRecipe.title || !newRecipe.description || !newRecipe.ingredients) {
      alert('Please fill in all fields');
      return;
    }

    // Create a new recipe object with a generated unique ID
    const recipe = { ...newRecipe, id: uuidv4() };

    // Update the recipes state by adding the new recipe
    setRecipes((prevRecipes) => [...prevRecipes, recipe]);
  };

  const handleDeleteRecipe = (recipeId) => {
    // Filter out the recipe with the given id
    const updatedRecipes = recipes.filter((recipe) => recipe.id !== recipeId);

    // Update the recipes state with the updated array
    setRecipes(updatedRecipes);
  };

  return (
    <div>
      {recipes && recipes.length > 0 ? (
        <table className="table mt-5">
          <thead>
            <tr>
              <th>Title</th>
              <th>Description</th>
              <th>Ingredients</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {recipes.map((recipe, index) => (
              <tr key={index}>
                <td>{recipe.title}</td>
                <td>{recipe.description}</td>
                <td>{recipe.ingredients}</td>
                <td>
                  <button className="btn btn-warning btn-sm me-2" onClick={() => onEdit(recipe.id)}>
                    Edit
                  </button>
                  <button className="btn btn-danger btn-sm" onClick={() => handleDeleteRecipe(recipe.id)}>
                    Delete
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      ) : (
        <p>No recipes found. Add a recipe.</p>
      )}

      <h3>Add Recipe</h3>
      <RecipeForm onSubmit={handleAddRecipe} setRecipes={setRecipes} />
    </div>
  );
};

export default RecipeList;
