import React, { useState, useEffect } from 'react';
import RecipeList from './RecipeList';
import { db } from '../firebase';

const RecipeListContainer = () => {
  const [recipes, setRecipes] = useState([]);

  useEffect(() => {
    // Fetch recipes from the database or any other data source
    // and update the `recipes` state
    const fetchRecipes = async () => {
      // Example code using Firestore
      const querySnapshot = await db.collection('recipes').get();
      const fetchedRecipes = querySnapshot.docs.map((doc) => ({
        id: doc.id,
        ...doc.data(),
      }));
      setRecipes(fetchedRecipes);
    };

    fetchRecipes();
  }, []);

  const handleEdit = (recipeId) => {
    // Handle edit functionality for a recipe with the given `recipeId`
    console.log('Edit recipe:', recipeId);
  };

  const handleDelete = (recipeId) => {
    // Handle delete functionality for a recipe with the given `recipeId`
    console.log('Delete recipe:', recipeId);
  };

  return (
    <div>
      <h1>Recipe List</h1>
      <RecipeList recipes={recipes} onEdit={handleEdit} onDelete={handleDelete} />
    </div>
  );
};

export default RecipeListContainer;
