import React, { useEffect, useState } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import { auth, onAuthStateChanged } from './firebase';
import LoginPage from './components/Authentication/LoginPage';
import RegisterPage from './components/Authentication/RegisterPage';
import Navbar from './components/Navbar';
import RecipeForm from './components/RecipeForm';
import RecipeList from './components/RecipeList';
import PrivateRoute from './components/PrivateRoute';
import Spinner from 'react-bootstrap/Spinner';

function App() {
  const [user, setUser] = useState(null);
  const [recipes, setRecipes] = useState([]);
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    const unsubscribe = onAuthStateChanged(auth, (user) => {
      setUser(user);
      setIsLoading(false);
    });

    return () => {
      unsubscribe();
    };
  }, []);

  const handleAddRecipe = (newRecipe) => {
    setRecipes((prevRecipes) => [...prevRecipes, newRecipe]);
  };

  const handleEditRecipe = (id, updatedRecipe) => {
    setRecipes((prevRecipes) =>
      prevRecipes.map((recipe) => (recipe.id === id ? updatedRecipe : recipe))
    );
  };

  const handleDeleteRecipe = (id) => {
    setRecipes((prevRecipes) => prevRecipes.filter((recipe) => recipe.id !== id));
  };

  return (
    <Router>
      <div className="container my-5">
        <div className="card p-4">
          <h1 className="mb-4">Recipe Book</h1>
          <Navbar user={user} />
          {isLoading ? (
            <Spinner animation="border" variant="primary" />
          ) : (
            <Routes>
              <Route
                path="/"
                element={<RecipeList user={user} recipes={recipes} onDelete={handleDeleteRecipe} />}
              />
              <PrivateRoute
                path="/add-recipe"
                element={<RecipeForm onSubmit={handleAddRecipe} />}
                authenticated={user !== null}
                fallback="/login"
              />
              <PrivateRoute
                path="/edit-recipe/:id"
                element={<RecipeForm onSubmit={handleEditRecipe} />}
                authenticated={user !== null}
                fallback="/login"
              />
              <Route
                path="/login"
                element={user ? <Navigate to="/" /> : <LoginPage setUser={setUser} />}
              />
              <Route
                path="/register"
                element={user ? <Navigate to="/" /> : <RegisterPage setUser={setUser} />}
              />
            </Routes>
          )}
        </div>
      </div>
    </Router>
  );
}

export default App;
