import React, { useState } from 'react';

const RecipeList = ({ recipes, onEdit, onDelete }) => {
  const [newRecipe, setNewRecipe] = useState({ title: '', description: '', ingredients: '' });

  const handleInputChange = (event) => {
    const { name, value } = event.target;
    setNewRecipe((prevRecipe) => ({ ...prevRecipe, [name]: value }));
  };

  const handleAddRecipe = () => {
    // Validate if any of the fields are empty
    if (!newRecipe.title || !newRecipe.description || !newRecipe.ingredients) {
      alert('Please fill in all fields');
      return;
    }

    // Add logic to handle adding a new recipe, such as calling an API or updating the state
    // Here, you can use the `newRecipe` state to access the newly added recipe details
    // For example:
    // const recipe = { ...newRecipe, id: generateUniqueId() };
    // addRecipe(recipe);

    // Reset the newRecipe state after adding the recipe
    setNewRecipe({ title: '', description: '', ingredients: '' });
  };


  return (
    <div>
      {recipes && recipes.length > 0 ? (
        <table className="table mt-5">
          <thead>
            <tr>
              <th>Title</th>
              <th>Description</th>
              <th>Ingredients</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {recipes.map((recipe, index) => (
              <tr key={index}>
                <td>{recipe.title}</td>
                <td>{recipe.description}</td>
                <td>{recipe.ingredients}</td>
                <td>
                  <button className="btn btn-warning btn-sm me-2" onClick={() => onEdit(recipe.id)}>
                    Edit
                  </button>
                  <button className="btn btn-danger btn-sm" onClick={() => onDelete(recipe.id)}>
                    Delete
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      ) : null}

      <h3>Add Recipe</h3>
      <form>
        {/* Form fields */}
      </form>
    </div>
  );
};

export default RecipeList;
