import React, { useState } from 'react';
import firebase from 'firebase/compat/app';
import 'firebase/compat/auth';



function LoginPage() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');

  const handleEmailChange = (e) => {
    setEmail(e.target.value);
  };

  const handlePasswordChange = (e) => {
    setPassword(e.target.value);
  };

  const handleLogin = () => {
    // Implement the login logic using Firebase authentication
    // You can use the 'signInWithEmailAndPassword' method from Firebase

    // Example code (replace with your own Firebase authentication logic):
    firebase.auth().signInWithEmailAndPassword(email, password)
      .then((userCredential) => {
        // Login successful
        const user = userCredential.user;
        console.log('Logged in:', user);
        // Add any additional logic here (e.g., redirect to another page)
      })
      .catch((error) => {
        // Login failed
        console.error('Error logging in:', error);
        // Handle the error appropriately (e.g., display an error message)
      });
  };

  return (
    <div>
      <h2>Login Page</h2>
      <form>
        <div>
          <label>Email:</label>
          <input type="email" value={email} onChange={handleEmailChange} />
        </div>
        <div>
          <label>Password:</label>
          <input type="password" value={password} onChange={handlePasswordChange} />
        </div>
        <button type="button" onClick={handleLogin}>Login</button>
      </form>
    </div>
  );
}

export default LoginPage;
